declare
    l_geography_id number;
begin
    begin
    select geography_id
      into l_geography_id
      from eba_cust_customers
     where id = :P50_ID;
    exception when no_data_found then
        l_geography_id := null;
        return false;
    end;
    return (wwv_flow_utilities.is_build_option_enabled('Geography') and l_geography_id is not null);
end;

